'use strict';

const base = require('./base.js');

const listAll = () => {
  return base.get('/gists');
};

const listStarred = () => {
  return base.get('/gists/starred');
};

const fromUser = (username) => {
  let url = '/users/{0}/gists'.format(username);
  return base.get(url);
};

module.exports = {
  all: listAll,
  starred: listStarred,
  fromUser: fromUser
};
