'use strict';

const json = require('gisthrower-infrastructure').json;
const appRoot = require('app-root-path');

const filePath = appRoot.path + '\\auth.json';

const setCredentials = (username, token) => {
  let auth = {
    username: username,
    token: token
  };
  json.writeFile(filePath, auth);
};

const getCredentials = () => {
  return json.readFile(filePath);
};

module.exports = {
  setCredentials: setCredentials,
  getCredentials: getCredentials,
  filePath: filePath
};
