'use strict';

const Download = require('download');

const downloadSingleFile = (url, dest, callback) => {
  new Download().get(url).dest(dest).run(callback);
};

const downloadMultipleFile = (urls, dest, callback) => {
  let downloadStream = new Download();
  urls.forEach((url) => {
    downloadStream = downloadStream.get(url);
  });
  downloadStream.dest(dest);
  downloadStream.run(callback);
};

module.exports = {
  downloadSingleFile: downloadSingleFile,
  downloadMultipleFile: downloadMultipleFile
};
