'use strict';

const chalk = require('chalk');

const gistInformations = (rawData) => {
  let messageBlock = '{0}\n{1}\n{2}\n{3}';

  const delimiter = chalk.dim('+'.concat((Array(59).join('-')), '+'));
  let title = chalk.underline.bold.green('| Gist {0} With Id: {1}'.format(rawData.description, rawData.id));
  let access = chalk.green('| Access: {0}'.format((rawData.public ? 'Public' : 'Private')));
  let gitUri = chalk.green('| Pull URL: {0}'.format(rawData.git_pull_url));

  return messageBlock.format(delimiter, title, access, gitUri);
};

module.exports = {
  gistInformations: gistInformations
};
