'use strict';

const gistApi = require('gisthrower-api');
const mapper = require('gisthrower-infrastructure').messageMapper;
const util = require('util');

const usingOptions = (options) => {
  if (options.all) {
    return true;
  }
  return false;
};

const list = (options) => {
  if (usingOptions(options)) {
    console.log('do something using options');
  };

  gistApi.listAll()
    .then((chunk) => {
      const pritableArray = chunk.map(mapper.gistInformations);
      pritableArray.forEach((gist) => { console.log(gist); });
    })
    .catch((error) => {
      console.error(error);
    });
};

module.exports = list;
